// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract DonationPlatform {
    struct Campaign {
        address payable recipient;
        string metadataURI;
        uint256 goalAmount;
        uint256 raisedAmount;
        bool fundsReleased;
    }

    mapping(uint256 => Campaign) public campaigns;
    mapping(uint256 => mapping(address => uint256)) public donations;
    uint256 public campaignCount;

    event CampaignCreated(uint256 indexed id, address recipient, uint256 goal);
    event DonationMade(uint256 indexed campaignId, address donor, uint256 amount);
    event FundsReleased(uint256 indexed campaignId, uint256 amount);

    function createCampaign(
        address payable _recipient,
        string calldata _metadataURI,
        uint256 _goalAmount
    ) external returns (uint256) {
        uint256 id = campaignCount++;
        campaigns[id] = Campaign({
            recipient: _recipient,
            metadataURI: _metadataURI,
            goalAmount: _goalAmount,
            raisedAmount: 0,
            fundsReleased: false
        });
        emit CampaignCreated(id, _recipient, _goalAmount);
        return id;
    }

    function donate(uint256 _campaignId) external payable {
        require(msg.value > 0, "Must send ETH");
        Campaign storage c = campaigns[_campaignId];
        require(!c.fundsReleased, "Campaign closed");

        c.raisedAmount += msg.value;
        donations[_campaignId][msg.sender] += msg.value;

        emit DonationMade(_campaignId, msg.sender, msg.value);
    }

    function releaseFunds(uint256 _campaignId) external {
        Campaign storage c = campaigns[_campaignId];
        require(c.raisedAmount >= c.goalAmount, "Goal not reached");
        require(!c.fundsReleased, "Already released");

        c.fundsReleased = true;
        uint256 amount = c.raisedAmount;
        c.recipient.transfer(amount);

        emit FundsReleased(_campaignId, amount);
    }

    // View function to get campaign details
    function getCampaign(uint256 _campaignId) external view returns (
        address recipient,
        string memory metadataURI,
        uint256 goalAmount,
        uint256 raisedAmount,
        bool fundsReleased
    ) {
        Campaign storage c = campaigns[_campaignId];
        return (c.recipient, c.metadataURI, c.goalAmount, c.raisedAmount, c.fundsReleased);
    }
}
